function addImplicitField(s, field) {
    const r = RegExp("^" + field + ":", "mi")
    if (r.test(s)) {
        return s
    }
    // s = s.trim()
    let touched = false
    let value = replaceLast(s, /^\w+:[^]+?(?=\n\n)/m, (x) => {
        touched = true
        return x + "\n\n" + field + ":\n"
    })
    if (touched) {
        return value
    }
    return field + ":\n" + s.trim()
}

async function dashed(text, options = {}) {
    assignFresh(options, {
        implicitField: "code",
        removeStartingComments: true,
    })

    if (options.removeStartingComments) {
        text = text.replace(/^\/\/.+/gm, "")
    }
    if (options.implicitField) {
        text = addImplicitField(text, options.implicitField)
    }

    const ref = {
        hooks,
        transformers,
    }

    if (options.hooks) {
        Object.assign(ref.hooks, options.hooks)
    }

    ref.bindings = options.bindings || {}
    ref.vim = options.vim || {}
    const config = colonConfig(text, ref)

    const get = async (config) => {
        if (config.exit) {
            Deno.exit()
        } else if (config.use) {
            const module = await import(config.use)
            const fn = must(module, "default")
            return fn(getArg(config))
        } else {
            const modules = await loadModules(
                config.code,
                options,
            )
            const evaluator = ScopedEvaluator.from(modules)
            return await evaluator(config.code)
        }
    }
    const result = await get(config)
    return await finish(result, config, options)
}

