async function loadModules(code, options) {
    const modules = toArray(options.modules)

    for (const c of toArray2(options.dynamicImports)) {
        if (c.regex.test(code)) {
            const module = await import(c.file)
            if (c.name) {
                const p = { [c.name]: module.default || module }
                modules.push(p)
            } else {
                modules.push(module)
            }

            for (const file of toArray2(c.modules)) {
                const module = await import(file)
                modules.push(module)
            }
        }
    }
    return modules
}

